   global theme
   if theme == 0:
      ChangeBackgroundColor("#FFFFFF")
   elif theme == 1:
      ChangeBackgroundColor("#000000")
   elif theme == 2:
      ChangeBackgroundColor("#EF7DB6")
   elif theme == 3:
      ChangeBackgroundColor("#29705C")
   elif theme == 4:
      ChangeBackgroundColor("#4248A6")
   elif theme == 5:
      ChangeBackgroundColor("#721717")
   else:
      ChangeBackgroundColor("#FFFFFF")
      theme = 0

