def getThemeColor():
   global theme
   if theme == 0:
      return "#FFFFFF"
   elif theme == 1:
      return "#000000"
   elif theme == 2:
      return "#EF7DB6"
   elif theme == 3:
      return "#29705C"
   elif theme == 4:
      return "#4248A6"
   elif theme == 5:
      return "#721717"
   else:
      return "#FFFFFF"
      theme = 0

